mapper.select:
SELECT *
FROM user user
WHERE user_nickname = #{userNickname}

mapper.update:
UPDATE user
SET user_nickname=#{userNickname}, user_pw=#{userPw}, weight_goal=#{weightGoal}, calories_goal=#{caloriesGoal},
user_tel=#{userTel}, user_mail=#{userMail}, user_height=#{userHeight}, user_weight=#{userWeight}
WHERE user_id=#{userId}

UPDATE user
SET user_nickname=#{userNickname}, user_mail=#{userMail}
WHERE user_id=#{userId}

UPDATE user
SET user_attendance = user_attendance + 1
WHERE user_id = #{userId}

mapper.select:
SELECT user_attendance
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET user_point = CASE WHEN user_attendance = 7 THEN user_point + 10 ELSE user_point END
WHERE user_id = #{userId}

mapper.select:
SELECT user_point
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET user_attendance = 0
WHERE user_id = #{userId}
